"use strict";

var fs = require("fs"),
    fluid = require("infusion"),
    gpii = fluid.registerNamespace("gpii");

fluid.defaults("gpii.flowManager.cloudBased.fullSHA.handler", {
    gradeNames: ["kettle.request.http"],
    members: {
        fileName: "gpii-revision.json"
    },
    invokers: {
        handleRequest: {
            funcName: "gpii.flowManager.cloudBased.fullSHA.handler.handleRequest",
            args: ["{gpii.flowManager.cloudBased}", "{that}"]
        }
    }
});

gpii.flowManager.cloudBased.fullSHA.handler.reportError = function (that, errMsg) {
    fluid.log ("Error retrieving full git revision: '" + that.fileName + "'");
    that.events.onError.fire({
        isError: true,
        message: "Error retrieving full git revision: " + errMsg,
        statusCode: 404
    });
};

gpii.flowManager.cloudBased.fullSHA.handler.handleRequest = function (flowmanager, that) {
    var result = {};
    var errMsg = null;
    debugger;
    try {
        result = JSON.parse(fs.readFileSync(that.fileName, "utf-8"));
        if (result.sha256.length === 0) {
            errMsg = "Missing SHA256";
        }
    } catch (e) {
        errMsg = e.message;
    };
    if (!errMsg) {
        that.events.onSuccess.fire(result);
    } else {
        gpii.flowManager.cloudBased.fullSHA.handler.reportError(that, errMsg);
    }
};
